<template>
    <div class="dashboard-library">
        <div class="dashboard-library-header">
            <div class="dashboard-library-header-label">图库</div>
            <img class="dashboard-library-header-icon" :src="closeLibraryIcon" @click="handleCloseLibrary"/>
        </div>
        <div class="dashboard-library-container-scroll" ref="containerRef" @scroll="handleLoadingMore">
            <div class="dashboard-library-container">
                <div class="dashboard-library-container-list" v-show="list.length">
                    <div class="dashboard-library-container-item" v-for="(item, index) in list" :key="index" @click.stop.prevent="() => handlePreview(index)">
                        <img :src="item?.downloadUrl" class="dashboard-library-container-item-img" />
                    </div>
                </div>
                <Empty v-show="!list.length" style="margin: 249px auto 0;"/>
                <!-- <Gallery /> -->
            </div>
        </div>
        <!-- <Gallery v-show="isShowGallery" :imageList="list" v-to-drag="dragOptions"/> -->
    </div>
</template>

<script setup>
import { ref, onMounted, nextTick } from 'vue'
import {ToDragDirective} from '@howdyjs/to-drag'
import Empty from '~components/dashboard/empty/index.vue'
import Gallery from '~components/dashboard/gallery/index.vue'
import closeLibraryIcon from 'data-base64:~assets/toggle.svg';
import { useGlobalState } from '~store/global';
import { get } from '~components/fetch';
import * as _ from 'lodash-es'

const vToDrag = ToDragDirective;
const { setIsOpenLibrary, isShowGallery, setIsShowGallery, setPreviewIndex, 
    setImgList,
    setLevelUp,
    setMessage,
} = useGlobalState()

const containerRef = ref()

const list = ref([
    // { src: 'data:image/jpeg;base64,'},
    // { src: 'data:image/jpeg;base64,'},
    // { src: 'data:image/jpeg;base64,'},
    // { src: 'data:image/jpeg;base64,'},
    // { src: 'data:image/jpeg;base64,'},
    // { src: 'data:image/jpeg;base64,'},
    // { src: 'data:image/jpeg;base64,'},
    //  { src: 'data:image/jpeg;base64,'},
    //   { src: 'data:image/jpeg;base64,'},
    //    { src: 'data:image/jpeg;base64,'},
    //     { src: 'data:image/jpeg;base64,'},
    //      { src: 'data:image/jpeg;base64,'},
    //       { src: 'data:image/jpeg;base64,'},
    //        { src: 'data:image/jpeg;base64,'},
    //         { src: 'data:image/jpeg;base64,'},
    //          { src: 'data:image/jpeg;base64,'},
    //           { src: 'data:image/jpeg;base64,'},
    //            { src: 'data:image/jpeg;base64,'},
    //             { src: 'data:image/jpeg;base64,'},
    //              { src: 'data:image/jpeg;base64,'},
])
const pageNum = ref(1)
const nomore = ref(false)

const dragOptions = ref({
    moveCursor: false,
    // adsorb: 2,
    positionMode: 2,
    // adsorbOffset: 10,
    adsorbOffset: 0,
    transitionDuration: 800,
    transitionTimingFunction: 'cubic-bezier(0.34, -0.37, 0.73, 1.38)'
  })

const handleCloseLibrary = () => {
    setIsOpenLibrary(false)
}

const handlePreview = (index) => {
    setIsShowGallery(true)
    setPreviewIndex(index)
    setLevelUp(1)
}
const scrollIntoView = (elm) => {
    elm?.scrollIntoView?.()
}
const queryFirstMessagesElm = (len = 0) => {
    return document.querySelectorAll('.dashboard-library-container-item')[len]
}
const queryLibrary = async (pageNo = 1, pageSize = 10) => {
    const currentScrollTop = containerRef.value.scrollTop;
    const res = await get(`/tutor/v1/files?pageNo=${pageNo}&pageSize=${pageSize}`)
    if(res.code === 0) {
        if(res.data.records?.length === 0) {
            setMessage({ type: 'warning', message: '已全部加载完'})
            return
        }
        pageNum.value = res.data.pageNo;
        if(pageNo === 1) {
            list.value = res?.data?.records || []
        } else {
            // const len = list.value?.length;
            list.value = [...list.value, ...res?.data?.records]
            // nextTick(()=> {
            //     containerRef.value.scrollTop = currentScrollTop
            //     // scrollIntoView(queryFirstMessagesElm(len)) 
            // })
        }
        setImgList(list.value)
        nextTick(()=> {
            containerRef.value.scrollTop = currentScrollTop
        })
    }
}

const handleLoadingMore = _.throttle(async () => {
    const scrollTop = containerRef.value.scrollTop;
    const scrollHeight = containerRef.value.scrollHeight;
    const clientHeight = containerRef.value.clientHeight;
    const buffer = 0;

    if(scrollTop + clientHeight + buffer >= scrollHeight) {
        console.log('scrollTop--scrollHeight', scrollTop , clientHeight , buffer , scrollHeight)
        await queryLibrary(pageNum.value + 1)
    }
    
}, 300)

onMounted(() => {
    queryLibrary()
})
</script>
